  { id: 3, description: "charger", quantity: 1, packed: false },
];*/

function Logo() {
  return <h1>üå¥ Far Away üß≥</h1>;
}

function Form({ handleAddItens }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function HandleSubmit(e) {
    e.preventDefault();

    if (!description) return;

    const newItem = { description, quantity, packed: false, id: Date.now() };
    handleAddItens(newItem);

    console.log(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={HandleSubmit}>
      <h3>What do you need for your trip? </h3>
      <select
        onChange={(e) => setQuantity(Number(e.target.value))}
        value={quantity}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map(
          (
            num //Aten√ßao
          ) => (
            <option key={num}>{num}</option>
          )
        )}
      </select>
      <input
        type="text"
        placeholder="Item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button>Add</button>
    </form>
  );
}

function PackingList({ array, handleDell, handleToggleItem }) {
  return (
    <div className="list">
      <ul>
        {array.map((item) => (
          <Item
            item={item}
            key={item.id}
            handleDell={handleDell}
            handleToggleItem={handleToggleItem}
          />
        ))}
      </ul>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      You have X items on your list, and you already packed X
    </footer>
  );
}

function Item({ item, handleDell, handleToggleItem }) {
  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => handleToggleItem(item.id)}
      />
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>

      <button onClick={() => handleDell(item.id)}>‚ùå</button>
    </li>
  );
}

export default function App() {
  const [itensList, setItensList] = useState([]);

  function handleAddItens(item) {
    setItensList((s) => [...s, item]);
  }

  function handleDell(id) {
    setItensList((s) => s.filter((item) => id !== item.id));
  }

  function handleToggleItem(id) {
    setItensList((s) =>
      s.map((item) =>
        item.id === id ? { ...item, packed: !item.packed } : item
      )
    );
  }
  console.log(itensList);

  return (
    <div className="app">
      <Logo />
      <Form handleAddItens={handleAddItens} />
      <PackingList
        array={itensList}
        handleDell={handleDell}
        handleToggleItem={handleToggleItem}
      />
      <Stats />
    </div>
  );
}

