import "./App.css";

let items = [{ n: 2, item: 'escolva de dentes' }, { n: 2, item: 'bermuda' }]




function App() {
  return (
    <>
      <h1>FAR AWAY</h1>


      <form className='add-form'>
        <h3>What do you need for your trip?</h3>
        <input type="number" value={1} />
        <input type="text" value='item' />
        <button>ADD</button>
      </form>


      <div className="list">
        <ul>
          {items.map((item) => <li><input type="checkbox" />{item.n} {item.item}</li>)}
        </ul>
        <button >SORT BY</button>
        <button >CLEAR LIST</button>
      </div>


      <div className='stats'>
        number of itens in your pack {items.length}
      </div>
    </>
  );
}

export default App;
