import "./App.css";
import { useState } from 'react'

let items = [
  { n: 2, item: "escolva de dentes" },
  { n: 2, item: "bermuda" },
];

function App() {
  const [n, setN] = useState()
  const [item, setItem] = useState()

  const handleSubmit = () => {
    const novoItem = { n, item }
    items.push(novoItem)

  }

  return (
    <>
      <h1>FAR AWAY</h1>

      <form className="add-form">
        <h3>What do you need for your trip?</h3>
        <input type="number" placeholder={0} value={n} onChange={(e) => setN(e.target.value)} />
        <input type="text" placeholder='item' value={item} onChange={(e) => setItem(e.target.value)} />
        <button onClick={() => handleSubmit()}>ADD</button>
      </form>

      <div className="list">
        <ul>
          {items.map((item) => (
            <li>
              <input type="checkbox" />
              {item.n} {item.item}
              <button>X</button>
            </li>
          ))}
        </ul>
        <button>SORT BY</button>
        <button>CLEAR LIST</button>
      </div>

      <div className="stats">number of itens in your pack {items.length}</div>
    </>
  );
}

export default App;
